        public void KatFromWycheproof(string ikm, string salt, string info, int length, string expectedOkm)
        {
            var ikmInBytes = StringToByteArray(ikm);
            var saltInBytes = StringToByteArray(salt);
            var infoInBytes = StringToByteArray(info);
            var expectedOkmInBytes = StringToByteArray(expectedOkm);
            var resultForAes = HkdfSha512.ForAes(ikmInBytes, saltInBytes, infoInBytes, length);
            var resultForCommitment = HkdfSha512.ForCommitment(ikmInBytes, saltInBytes, infoInBytes, length);
            Assert.Equal(expectedOkmInBytes, resultForAes);
            Assert.Equal(expectedOkmInBytes, resultForCommitment);
        }
        
        private static byte[] StringToByteArray(string hex) {
            return Enumerable.Range(0, hex.Length)
                .Where(x => x % 2 == 0)
                .Select(x => Convert.ToByte(hex.Substring(x, 2), 16))
                .ToArray();
        }
